// https://blog.bagooon.com/?p=1888
// default thumbnail when fails
const imgIcon = 'data:image/svg+xml;base64,';

/**
 * Make a thumbnail from an image file
 * @param {string} file 
 * @param {Element} canvas 
 * @returns {Promise}
 */
function makeThumbnail(file, canvas, width = 24, height = 24) {
  // returns asynchronous process
  return new Promise((resolve) => {
    try {
      if (canvas.width) width = canvas.width;
      if (canvas.height) height = canvas.height;

      const ctx = canvas.getContext('2d')

      const reader = new FileReader()

      reader.readAsDataURL(file)

      reader.onload = () => {
        const img = new Image()
        img.src = reader.result
        img.onload = () => {
          ctx.drawImage(img, 0, 0, width, height)
          resolve(canvas.toDataURL('image/jpeg', 0.5))
        }

        img.onerror = () => {
          throw new Error('error')
        }
      }
    } catch (err) {
      resolve(imgIcon)
    }
  })
};

export {makeThumbnail};